import React, { useState } from 'react'
import { storage } from '../../Firebase/index';

const proxyUrl = "https://cors-anywhere.herokuapp.com/";
const url = "https://us-central1-project-share-8df06.cloudfunctions.net/api/";

const SignUp = (props) => {
    const [handle, setHandle] = useState('');
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [confirm, setConfirm] = useState('');
    const [errors, setErrors] = useState('');
    const [image, setImage] = useState(null);
    const [url, setUrl] = useState('');
    const [progress, setProgress] = useState(0);

    const handleChange = e => {
        if (e.target.files[0]) {
            setImage(e.target.files[0]);
        }
    };

  const handleUpload = () => {
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      error => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then(url => {
            setUrl(url);
          });
      }
    );
  };

    const submit = () => {
        fetch(proxyUrl + url + 'signup', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: email,
                password: pass,
                confirmPassword: confirm,
                userHandle: handle,
                imageLink: url
            })
        })
            .then(res => res.json())
            .then(data => {
                if(data.idToken) {
                    sessionStorage.setItem('token', data.idToken);
                }
                // handle
                else if(data.handle) {
                    setErrors(data.handle);
                }
                // error
                else if(data.error) {
                    setErrors(data.error);
                }
                // confirmPassword
                else if(data.confirmPassword) {
                    setErrors(data.confirmPassword);
                }
            })
    }

    return (
        <div>
            <input value={handle} placeholder="User Handle" onChange={e => setHandle(e.target.value)} />
            <input value={email} placeholder="Email" onChange={e => setEmail(e.target.value)} />
            <input value={pass} placeholder="Password" onChange={e => setPass(e.target.value)} />
            <input value={confirm} placeholder="Confirm Password" onChange={e => setConfirm(e.target.value)} />
            <button onClick={submit}>Sign Up</button>
            <div>
                <progress value={progress} max="100" />
                <br />
                <br />
                <input type="file" onChange={handleChange} />
                <button onClick={handleUpload}>Upload</button>
                <br />
                {url}
                <br />
                <img src={url || "http://via.placeholder.com/300"} alt="firebase-image" />
            </div>
            {errors}
        </div>
    )
}

export default SignUp;
