import React, { useState } from 'react'
import {
    useHistory
} from 'react-router-dom'; // For Redirecting Page 

const Navigation = () => {
    let history = useHistory(); 
    const [value, setValue] = useState(null);

    const search = (e) => {
        if(value !== null) {
            history.push(`/user/${value}`)
        } else {
            e.preventDefault();
        }
    }

    return (
        <div>
            <form onSubmit={search}>
                <button className='searchButton'>Search</button>
                <input 
                    className='searchBar' 
                    value={value} 
                    onChange={(e) => setValue(e.target.value)} 
                    placeholder="Search by Username"
                />
            </form>
            <button onClick={() => history.push('/home')}></button>
            <button onClick={() => history.push('/trending')}></button>
            <button onClick={() => history.push('/leaderboard')}></button>
            <button onClick={() => history.push('/topPosts')}></button>
            <button onClick={() => history.push('/categories')}></button>
        </div>
    )
}

export default Navigation;
