import React, { useEffect, useState } from 'react';
import Loading from '../Loading/Loading';
import CommentComponent from './Commenting';

const proxyUrl = "https://cors-anywhere.herokuapp.com/";
const url = "https://us-central1-project-share-8df06.cloudfunctions.net/api/";

const Commenting = (props) => {
    const [postData, setPost] = useState({
        id: props.id,
    })
    const [comments, setComments] = useState(null);
    const [comment, setComment] = useState('');

    useEffect(() => {
        fetch(proxyUrl + url + 'getComment', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "User-Agent": "PostmanRuntime/7.26.5",
                "Accept": "*/*",
                "Accept-Encoding": "gzip, deflate, br",
                "Connection": "keep-alive"
            },
            body: JSON.stringify(postData)
        })
        .then(res => res.json())
        .then(data => {
            setComments(data);
        })
    }, [])

    const post = () => {
        fetch(proxyUrl + url + '/createComment', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "User-Agent": "PostmanRuntime/7.26.5",
                "Accept": "*/*",
                "Accept-Encoding": "gzip, deflate, br",
                "Connection": "keep-alive",
                "Authorization": `Bearer eyJhbGciOiJSUzI1NiIsImtpZCI6IjIzNzA1ZmNmY2NjMTg4Njg2ZjhhZjkyYWJiZjAxYzRmMjZiZDVlODMiLCJ0eXAiOiJKV1QifQ..gyahUcMvybmely4osmEhgPaxm6fs4TJb3-RKGUKVRik_Ba0wBdsqarRrmqvupDGcY_5FO9rqS5sYm2e750V5Xm5q8-eZrGKwB5_PBxVYpY6d2w4Yrz1iqyCtQIOgj_ALvPvB5iNfE4qlgSy9zrXXDPnzSKuvUu6wErVQmOEba_-1snsJXymvuSZGjvLeW9Ya2p8CnIEuWIIF5CZsWdP_W6spKXnNxpTYzb-cZRZOsRjYui5n-SZCmNRUk9eRE24ZHCI2p4lKDwU03DCvda5VV1qOWIjTc5AsS0HP_clDJ-mzDdspi2mUOaVuUuyHMhzc1NZaU5cjDRMi5dUih1AIoA`
            },
            body: JSON.stringify({
                id: props.id,
                body: comment
            })
        })
        .then(res => res.json())
        .then(data => console.log(data))
        .catch(err => console.log(err));
    }

    let posting = (
        <div>
            <input 
                placeholder="Comment whatever you would like but remember you must be logged in"
                value={comment}
                onChange={e => setComment(e.target.value)}
            />
            <button onClick={post}>Comment</button>
        </div>
    )

    if(comments == null) {
        return (
            <div>
                <Loading />
                { posting }
            </div>
        )
    } else if(comments.error === "Could not find post") {
        return (
            <div>
                <h1>Could not find any comments, Be the first to comment!</h1>
                { posting }
            </div>
        )
    }

    return (
        <div>
            {
                comments.map(c => <li>{c.body}</li>)
            }
            { posting }
        </div>
    )
}

export default Commenting;
