import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import Loading from '../Loading/Loading';
import CommentComponent from './Commenting';

const proxyUrl = "https://cors-anywhere.herokuapp.com/";
const url = "https://us-central1-project-share-8df06.cloudfunctions.net/api/";

const Commenting = props => {
    const [postData, setPost] = useState({
        id: props.id,
    })
    const [comments, setComments] = useState(null);
    const [comment, setComment] = useState('');

    const token = useSelector(state => state.idToken);

    useEffect(() => {
        fetch(proxyUrl + url + 'getComment', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "User-Agent": "PostmanRuntime/7.26.5",
                "Accept": "*/*",
                "Accept-Encoding": "gzip, deflate, br",
                "Connection": "keep-alive"
            },
            body: JSON.stringify(postData)
        })
        .then(res => res.json())
        .then(data => {
            console.log(data);
            setComments(data);
        })
        .catch(err => {
            console.log(err);
        })
    }, [])

    const post = () => {
        console.log("Hello");
    }

    let posting = (
        <div>
            <input 
                placeholder="Comment whatever you would like but remember you must be logged in"
                value={comment}
                onChange={e => setComment(e.target.value)}
            />
            <button onClick={post}>Comment</button>
        </div>
    )

    const check = () => {
        if(comments == null) {
            
            return (
                <div>
                    <h1>Could not find any comments, Be the first to comment!</h1>
                    { posting }
                </div>
            )
        }
        else {
            return (
                <div>
                    {
                        comments ?  
                        comments.map(c => 
                            <li key={c.id}>{c.body} Author: {c.author}</li>
                        )
                        : <Loading />
                    }
                    { posting }
                </div>
            )
        }
    }

    
}

export default Commenting;
