import React, { useState } from 'react';
import { storage } from '../Firebase/index';

const ImageUp = () => {
    const [handle, setHandle] = useState(null);

    const handleChange = e => {
        if(e.target.value[0]) {
            setHandle(e.target.value[0]);
        }
    }

    const handleUpload = () => {
        const uploadTask = storage.ref(`images/${handle.name}`).put(handle);
        uploadTask.on(
            "state_changed",
            snapshot => {},
            error => {
                console.error(error);
            },
            () => {
                storage
                    .ref("images")
                    .child(handle.name)
                    .getDownloadURL()
                    .then(url => {
                        console.log(url);
                    })
            }
        )
    };

    return (
        <div>
            <input type="file" onChange={handleChange} />
            <button onClick={handleUpload}>Upload</button>
        </div> 
    )
}

export default ImageUp;
