import React from 'react'
import HomePosts from '../../Components/Posts/Posts';

const proxyUrl = "https://cors-anywhere.herokuapp.com/";
const url = "https://us-central1-project-share-8df06.cloudfunctions.net/api/";

class Home extends React.Component {
    state = {
        posts: []
    }

    componentDidMount() {
        fetch(proxyUrl + url + 'getHome')
            .then(res => res.json())
            .then(data => this.setState({ posts: data }))
            .catch(() => console.error('Could not connect to ' + url + 'getHome please try again'))
    }

    render() {
        return (
            <div>
                { 
                    this.state.posts.map(post => {
                        <HomePosts title={post.title} author={post.author} category={post.category} body={post.body} createdAt={post.createdAt} />
                    }) 
                }
            </div>
        )
    }
};

export default Home;
