import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import CommentComponent from './Commenting';

const proxyUrl = "https://cors-anywhere.herokuapp.com/";
const url = "https://us-central1-project-share-8df06.cloudfunctions.net/api/";

const Commenting = props => {
    const [postData, setPost] = useState({
        id: props.id,
    })
    const [comments, setComments] = useState(null);
    const [comment, setComment] = useState('');

    const token = useSelector(state => state.idToken);

    useEffect(() => {
        fetch(proxyUrl + url + 'getComment', {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                "User-Agent": "PostmanRuntime/7.26.5",
                "Accept": "*/*",
                "Accept-Encoding": "gzip, deflate, br",
                "Connection": "keep-alive"
            },
            body: JSON.stringify(postData)
        })
        .then(res => res.json())
        .then(data => {
            console.log(data);
            setComments(data);
        })
        .catch(err => {
            console.log(err);
        })
    }, [])

    return (
        <div>
            { 
                comments !== null ?
                comments.map(c => <li key={c.id}>Author: {c.author}  {c.body}</li>)
                : null
            }
            <input 
                placeholder='Comment anything but make sure you are logged in!'
                value={comment}
                onChange={e => setComment(e.target.value)}
            />
            <button>Comment</button>
        </div>
    )
}

export default Commenting;
