import React from 'react'
import { useSelector, useDispatch } from 'react-redux'

const Counter = () => {
    const counter = useSelector(state => state.counter);
    const dispatch = useDispatch();

    return (
        <div>
            <button onClick={() => dispatch({ type: 'SET_COUNT', newCount: newCount + 1 })}>Increment</button>
            <button onClick={() => dispatch({ type: 'SET_COUNT', newCount: newCount - 1 })}>Decrement</button>
            {counter}
            <button onClick={() => dispatch({ type: 'SET_COUNT', newCount: newCount - 1 })}>Decrement</button>
            {counter}
        </div>
    )
}

export default Counter;
