const initState = {
    idToken: '',
    counter: '',
}

const rootReducer = (state = initState, action) => {
    if(action.type === 'SET_NEW_TOKEN') {
        let newToken = action.token;
        return {
            ...state,
            idToken: newToken
        }
    }
    if (action.type === 'SET_COUNT'){
        let newCount = action.count;
        return {
            ...state,
            counter: newCount
        }
    }

    return state;
}

export default rootReducer;
