import React from 'react';

const Fetching = () => {

    const fetch = () => {
        fetch('http://localhost:4000/graphql', {
            methods: 'POST', 
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                query: `
                    {
                        homePage {
                            id
                            title
                            category
                            author
                        }
                    }
                `
            })
        })
        .then(res => res.json())
        .then(data => console.log(data));
    }

    return (
        <div>
            <button onClick={fetch}>Fetch</button>
        </div>
    )
}

export default Fetching;
