import React, { useEffect, useState } from 'react';
import { Fetch } from '../../Tools';
import Post from '../Posts/Posts';

const CategoryList = (props) => {
    const [post, setPost] = useState(null);
    const [posted, setPosted] = useState(true);
    const [posts, setPosts] = useState(null);

    const getPost = () => {
        const scoped = async () => {
            const res = await Fetch(`
                {
                    categoryPost(category: "${props.title}") {
                        id
                        title
                        category
                        body
                        author
                        likes
                    }
                }
            `)
            setPosts(res.categoryPost);
            console.log(res.categoryPost);
        }
        scoped();
    }


    useEffect(() => {
        if(posted) {
            setPost(posts);
        } else if(!posted) {
            setPost(null);
        }
    }, [post])

    return (
        <div className="category-list-item">
            <h1>{props.title}</h1>
            <button onClick={() => setPosted(!posted)}>Display Posts</button>
            {
                post ? <Post author={post.author} title={post.title} category={post.category} likes={post.likes} id={post.id}>{ post.body }</Post> : null
            }
        </div>
    );
};

export default CategoryList;
