import React, { useEffect, useState } from 'react';
import { Fetch } from '../../Tools';

const Commenting = React.memo((props) => {
    const [comments, setComments] = useState(null);
    const [comment, setComment] = useState('');

    useEffect(() => {
        const scoped = async () => {
            const res = await Fetch(`
                {
                    getComments(id:"${props.id}") {
                        id
                        body
                        author
                    }
                }
            `);
            setComments(res.getComments);
        }
        scoped();
    }, [])

    const idToken = sessionStorage.getItem('token');

    let loadAll = () => {
        changeBut();
    }

    const post = () => {
        if(idToken == null | undefined) {
            alert('Not Logged In')
        } else {
            const scoped = async () => {
                const res = await Fetch(`
                    mutation {

                    } 
                `)
            }

            scoped();
        }
    }

    const handleChange = (e) => {
        if(e.key == "Enter") {
            post();
        }
    }

    let input = (
        <div>
            <input 
                value={comment}
                onChange={e => setComment(e.target.value)}
                onKeyPress={handleChange}
            />
            <button onClick={post}>Comment</button>
        </div>
    );

    if(idToken == null) {
        input = (
            <div>
                <input disabled="true" />
                <button disabled="true">Comment</button>
            </div>
        )
    }

    if(comments == null) {
        return (
            <div>
                { input }
            </div>
        )
    } 

    return (
        <div>
            { comments.map(comment => <Commenting author={comment.author} body={comment.body} />) }
        </div>
    )
});

export default Commenting;
