import React, { useEffect, useState } from 'react';
import { Fetch } from '../../Tools';

const Commenting = (props) => {
    const [comments, setComments] = useState(null);

    useEffect(() => {
        const scoped = async () => {
            const res = await Fetch(`
                {
                    getComments(id: "${props.id}) {
                        id
                        author
                        body
                    }
                } 
            `);
            console.log(res);
        }

        scoped();
    }, [])

    return (
        <div>
            Comments
        </div>
    )

};

export default Commenting;
