const initState = {
    idToken: '',
    userName: '',
    password: '',
}

const action = {
    type: 'SET_NEW_USERNAME',
    userName: 'Om'
}

const rootReducer = (state = initState, action) => {
    if(action.type === 'SET_NEW_TOKEN') {
        let newToken = action.token;
        return {
            ...state,
            idToken: newToken
        }
    } 
    if(action.type === 'SET_NEW_USERNAME') {
        let username = action.userName;
        return {
            ...state,
            userName: username 
        }
    }
    return state;
}

export default rootReducer;
