export const Fetch = async (req) => {
    /*
    {
        homePage {
            id
            title
            category
        }
    }
    */
    let msg;

    await fetch('http://localhost:4000/graphql', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            query: `
                {
                    homePage {
                        id
                        title
                        category
                    }
                }
            `
        })
    })
        .then(res => res.json())
        .then(data => {
            msg = data.data;
        });

    return msg;
};
