import React, { useEffect, useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import {
    useHistory,
    useParams
} from 'react-router-dom';
import Loading from '../../Components/Loading/Loading';
import Post from '../../Components/Posts/Posts';
import { Fetch } from '../../Tools';

const User = () => {
    const [user, setUser] = useState({});
    const [supporters, setSupporters] = useState(null);
    const [posts, setPosts] = useState(null);
    let [share, setShare] = useState(null);

    const [supported, setSupported] = useState(false);

    const unsupport = () => {
        setSupported(!supported);
        // fetch(proxyUrl + url + 'unfollowUser', {
        //     method: 'POST',
        //     headers: {
        //         "Content-Type": "application/json",
        //         "User-Agent": "PostmanRuntime/7.26.5",
        //         "Accept": "*/*",
        //         "Accept-Encoding": "gzip, deflate, br",
        //         "Connection": "keep-alive"
        //     },
        //     body: JSON.stringify({
        //         supporters: supporters,
        //         handle: user.handle,
        //         email: user.email,
        //         userId: user.userId,
        //         id: user.userId
        //     })
        // })
        // .then(() => {
        //     setSupporters(supporters - 1);
        // })
    }

    const support = () => {
        setSupported(!supported);
        // fetch(proxyUrl + url + 'followUser', {
        //     method: 'POST',
        //     headers: {
        //         "Content-Type": "application/json",
        //         "User-Agent": "PostmanRuntime/7.26.5",
        //         "Accept": "*/*",
        //         "Accept-Encoding": "gzip, deflate, br",
        //         "Connection": "keep-alive"
        //     },
        //     body: JSON.stringify({
        //         supporters: supporters,
        //         handle: user.handle,
        //         email: user.email,
        //         userId: user.userId,
        //         id: user.userId
        //     })
        // })
        // .then(() => {
        //     setSupporters(supporters + 1);
        // })
    }

    let supportButton = (
        <button onClick={support}>Support</button>
    )

    const { userHandle } = useParams();
    
    const history = useHistory();

    if(supported) {
        supportButton = (
            <button onClick={unsupport}>Unsupport</button>
        );
    } else {
        supportButton = (
            <button onClick={support}>Support</button>
        )
    }

    useEffect(() => {
        // fetch(proxyUrl + url + 'getUser', {
        //     method: 'POST',
        //     headers: {
        //         "Content-Type": "application/json",
        //         "User-Agent": "PostmanRuntime/7.26.5",
        //         "Accept": "*/*",
        //         "Accept-Encoding": "gzip, deflate, br",
        //         "Connection": "keep-alive"
        //     },
        //     body: JSON.stringify({
        //         userHandle: userHandle
        //     })
        // })
        // .then(res => res.json())
        // .then(data => {
        //     if(typeof data == 'object') {
        //         if(Object.keys(data).length === 0) {
        //             history.push('/wronguser');
        //         } else {
        //             setUser(data.user);
        //             setShare(`localhost:3000/user/${data.user.handle}`)
        //             setSupporters(data.user.supporters);
        //         }
        //     }
        // })

        const scoped = async () => {
            const res = await Fetch(`
                {
                    user(handle: "${userHandle}") {
                        id
                        supporters
                        handle
                        email
                        imageUrl
                    }
                }
            `) 

            setUser(res.user);
            setSupporters(res.user.supporters)
            setShare(`localhost:3000/user/${res.user.handle}`)
        }

        scoped();
    }, [])

    const loadPost = () => {
        const scoped = async () => {
            console.log(user.handle);
            const res = await Fetch(`
                {
                    userPosts(handle:"${user.handle}"){
                        id
                        title
                        category
                        likes
                        body
                        author
                        imageUrl
                    }
                }
            `);
            setPosts(res.userPosts);
            if(res.userPosts) {
                history.push(`localhost:3000/wronguser`)
            }
        }
        scoped();
    }

    let userInfo = (
        <div>
            {
                user ? <div>
                    
                </div> : <Loading />
            }
            {user.handle} <br />
            {supporters} <br />
            { supportButton }
            <CopyToClipboard text={share}>
                <button>Copy Share Link</button>
            </CopyToClipboard>
            {user.bio}
            <img width="1000px" src={user.imageUrl ? user.imageUrl : null} />
            <button onClick={loadPost}>Load Posts</button>
            <div>
                {
                    posts ? 
                    posts.map(post => 
                        <Post
                            title={post.title} 
                            author={post.author} 
                            category={post.category}
                            likes={post.likes}
                            id={post.postId}
                            createdAt={post.createdAt}
                        >
                            {post.body}
                        </Post>
                    ) :
                    null
                }
            </div>
        </div>
    )

    return (
        <div>
            { Object.keys(user).length === 0 ? <Loading /> : userInfo}
        </div>
    )
}

export default User;
