import React, { useEffect } from 'react';

const Commenting = (props) => {
    const [createdAt, setCreatedAt] = useState(null);

    useEffect(() => {
        setCreatedAt(props.createdAt);
    })

    return (
        <div className="comment-box">
            Author: { props.author }<br />
            { props.body }
            { props.created }
        </div>
    )
}

export default Commenting;
