export const Fetch = (req) => {
    let send = '';

    fetch('http://localhost:4000/graphql', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            query: req
        })
    })
    .then(res => res.json())
    .then(data => {
        console.log(data);
        send = data;
    })

    return send;
};
