const initState = {
    idToken: '',
    counter: 0,
}

const rootReducer = (state = initState, action) => {
    if(action.type === 'SET_NEW_TOKEN') {
        let newToken = action.token;
        return {
            ...state,
            idToken: newToken
        }
    }
    if(action.type === "INCREMENT") {
        let newState = state.counter + 1;
        return {
            ...state,
            counter: newState
        }
    }   
    if(action.type === "DECREMENT") {
        let newState = state.counter - 1;
        return {
            ...state,
            counter: newState
        }
    }
    if(action.type === "SET_COUNT") {
        let newState = action.newCount;
        return {
            ...state,
            counter: newState
        }
    }
    return state;
}

export default rootReducer;
