import React, { useEffect, useState } from 'react'


const proxyUrl = "https://cors-anywhere.herokuapp.com/";
const aurl = "https://us-central1-project-share-8df06.cloudfunctions.net/api/";

const Navigation = () => {
    const [value, setValue] = useState(null);
    const [url, setUrl] = useState(null);

    const setting = e => {
        setValue(e.target.value);
        setUrl(`/user/${e.target.value}`)
    }

    let authButtons = (
        <a href="/auth">Authentication</a>
    )

    useEffect(() => {
        const data = sessionStorage.getItem('token');
        if(data !== '') {
            fetch(proxyUrl + aurl + 'getHandle', {
                method: 'POST',
                headers: {
                   "Authorization": `Bearer ${data}`
                }
            })
                .then(res => res.json())
                .then(data => {
                    console.log(data.handle);
                    if(data.handle) {
                        let authButtons = (
                            <div>
                                <h1>{data.handle}</h1>
                            </div>
                        )
                    }
                })
                .catch(err => {
                    console.log(err)
                })
        }
    }, [])

    return (
        <div className="nav">
            <header className="header">
                <a href="/">Project Sh@are</a>
                <a className="searchButton" href={url}>Search</a>
                <input
                    className='searchBar'
                    value={value}
                    onChange={e => setting(e)}
                    placeholder="Search by Username"
                />
                <a className="nav-link" href="/trending">Trending</a>
                <a className="nav-link" href="/leaderboard">Leaderboard</a>
                <a className="nav-link" href="/topPosts">Top Posts</a>
                <a className="nav-link" href="/categories">Categories</a>
                {authButtons}
            </header>
        </div>
    )
}

export default Navigation;
