import React, { useEffect, useState } from 'react';
import { Fetch } from '../../Tools';

const Home = () => {
    const [posts, setPosts] = useState(null);

    useEffect(() => {
        const scoped = async () => {
            const res = await Fetch(`
                {
                    homePage {
                        id
                        title
                        author
                        category
                        likes
                        body
                    }
                }
            `);

            setPosts(res.homePage);
        };

        scoped();
    }, [posts])

    return (
        <div>
            {
                posts ? 
                posts.map(post => 
                    <Post
                        title={post.title} 
                        author={post.author} 
                        category={post.category}
                        likes={post.likes}
                        id={post.postId}
                        createdAt={post.createdAt}
                        token={this.props.token}
                    >
                        {post.body}
                    </Post>
                )
                : <Loading />
            }
        </div>
    )
}

export default Home;
