import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { Fetch } from "../../Tools";

const Navigation = (props) => {
  const [value, setValue] = useState("");
  const [text, setText] = useState("Search by Author");
  const [toggle, setToggle] = useState(false);

  const click = () => {
    // let init = toggle;
    // init = !init;
    // setToggle(!toggle);
    // if (init == true) {
    //   setText("Search by Author");
    //   setProgress(0);
    // } else if (init !== true) {
    //   setText("Search by Category");
    //   setProgress(100);
    // }
  };

  const push = () => {
    if (toggle == true) {
      history.push(`/user/${value}`);
    } else if (toggle !== true) {
      history.push(`/category/${value}`);
    }
  };

  const [authentication, setAuthentication] = useState(null);

  const logout = () => {
    if (window.confirm("Are You Sure You want to logout")) {
      sessionStorage.clear();
      window.location.reload(false);
    }
  };

  let history = useHistory();

  useEffect(() => {
    const t = sessionStorage.getItem("token");
    if (t !== null) {
      const scoped = async () => {
        const res = await Fetch(`
                    {
                        tokenUser(token: "${t}") {
                            handle
                        }
                    } 
                `);
        if (res.tokenUser) {
          sessionStorage.setItem("handle", res.tokenUser.handle);
          setAuthentication(
            <div>
              <li className="handle">
                <a href={`http://localhost:3000/user/${res.tokenUser.handle}`}>
                  {res.tokenUser.handle}
                </a>
                <ul>
                  <li>
                    <a href="/newpost">New Post</a>
                  </li>
                  <li>
                    <a onClick={logout}>Logout</a>
                  </li>
                </ul>
              </li>
            </div>
          );
        } else {
          history.push("/auth");
        }
      };

      scoped();
    } else {
      setAuthentication(
        <a className="authentication" href="/auth">
          Authentication
        </a>
      );
    }
  }, []);

  const handleKeyPress = (e) => {
    if (e.key == "Enter") {
      push();
    }
  };

  useEffect(() => {
    console.log(toggle);
  }, [toggle])

  return (
    <div className="dropdown">
      <nav>
        <a className="logo" href="/home">
          Project Sh@are
        </a>
        <div>
          <div className="toggle">
            <label className="switch">
              <input value={toggle} type="checkbox" />
              <span class="slider round"></span>
            </label>
          </div>
          <input
            className="searchBar"
            value={value}
            onChange={(e) => setValue(e.target.value)}
            placeholder="Search: "
            onKeyPress={(e) => handleKeyPress(e)}
          />
          <button className="search-button" onClick={push}>
            Search
          </button>
        </div>
        <ul>
          <li>
            <a className="trending" href="/trending">
              Trending
            </a>
          </li>
          <li>
            <a className="leaderboard" href="/leaderboard">
              Leaderboard
            </a>
          </li>
          <li>
            <a className="categories" href="/categories">
              Categories
            </a>
          </li>
          <li>
            <a className="requests" href="/about">
              About
            </a>
          </li>
          <li>{authentication}</li>
        </ul>
      </nav>
    </div>
  );
};

export default Navigation;
