import React, { useEffect, useState } from 'react';
import { Fetch } from '../../Tools';


const Commenting = (props) => {
    const [comments, setComments] = useState(null);

    useEffect(() => {
        const scoped = async () => {
            const res = await Fetch(`
                {
                    getComments(id:"${props.id}") {
                        id
                        body
                        author
                        postId
                    }
                } 
            `);
            console.log(res.getComments);
            setComments(res.getComments);
        }

        scoped();
    })

    return (
        <div>
            {
                comments ? comments.map(c => <Commenting author={c.author} body={c.body} />) : <p>Loading...</p>
            }
        </div>
    )
};

export default Commenting;
